import { Link, useParams, useLocation } from "react-router-dom";
import '../index.css'
import db from "../../Database";

function CourseNavigation() {
  const courses = db.courses;
    const links = [
        "Home", "Modules", "Piazza", "Zoom Meetings", "Assignments", "Quizzes", "Grades", 
        "People", "Discussions", "Announcements", "Pages", "Files", "Rubrics", "Outcomes", 
        "Collaborations", "Syllabus", "Settings"
    ];
    const { courseId } = useParams();
    const { pathname } = useLocation();
    const course = courses.find(course => course._id === courseId);

    return (
        <div className="wd-scrollable">
            <div className="wd-account-main-page">
                <div className="container-fluid">
                    <div className="row align-items-start">
                        <div className="col-auto">
                            <i className="fa-solid fa-bars wd-custom-hamburger"></i>
                        </div>
                        <div className="col">
                            <ol className="breadcrumb">
                                <li className="breadcrumb-item wd-breadcrumb-header">
                                    <Link to={`/Kanbas/Courses/${courseId}`}>{course.name}</Link>
                                </li>
                                <li className="breadcrumb-item wd-breadcrumb-header-active" aria-current="page">
                                    {links.find(link => pathname.includes(link)) || "Home"}
                                </li>
                            </ol>
                        </div>
                    </div>
                    <div className="row">
                        <hr className="wd-custom-divider" />
                    </div>
                    <div className="row">
                        <div className="col col-sm-2">
                            <div className="wd-account-navigation">
                                {links.map((link, index) => (
                                    <div key={index} className={`wd-navigation-element ${pathname.includes(link) ? "wd-navigation-element-active" : ""}`}>
                                        <Link to={`/Kanbas/Courses/${courseId}/${link}`}>{link}</Link>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CourseNavigation;
